import React, { useEffect, useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Bookings.css";
import Navbar from "../NavBar/NavBar";
import Reservation from "../Reservations/Reservation";
import Footer from "../Footer/Footer";

const Bookings = () => {
    const [parkingSlots, setParkingSlots] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [bookedSlotId] = useState(null);
    const [selectedSlotId, setSelectedSlotId] = useState(null);
    const [selectedTimeSlot, setSelectedTimeSlot] = useState(""); 
    const [selectedDate, setSelectedDate] = useState(null); 

    const fetchAllData = async () => {
        setLoading(true);
        try {
            // Fetch all data concurrently
            const [parkingRes, latestRes, reservedRes] = await Promise.all([
                fetch("http://localhost:5001/api/parking/slots"),
                fetch("http://localhost:5001/api/slots/latest-booked-slot"),
                fetch("http://localhost:5001/api/reserved/slots"),
            ]);

            // Check if all responses are OK
            if (!parkingRes.ok || !latestRes.ok || !reservedRes.ok) {
                throw new Error("Failed to fetch one or more data sources");
            }

            // Convert responses to JSON
            const parkingData = await parkingRes.json();
            const latestData = await latestRes.json();
            const reservedData = await reservedRes.json();

            // Extract values
            const parkingSlots = parkingData.parkingSlots || [];
            const reservedSlots = reservedData.parkingSlots || [];
            const latestBookedSlotId = latestData.latestBookedSlotId;

            // Create a Map of reserved slots for quick lookup
            const reservedMap = new Map(reservedSlots.map(slot => [slot.id, slot]));

            // Merge the slot data ensuring reserved slots take priority
            const updatedSlots = parkingSlots.map(slot => {
                if (reservedMap.has(slot.id)) {
                    return reservedMap.get(slot.id); // Use reserved slot data
                }
                return slot; // Otherwise, keep existing parking slot data
            });

            // If latestBookedSlotId exists, mark it as occupied
            if (latestBookedSlotId) {
                const slotIndex = updatedSlots.findIndex(slot => slot.id === latestBookedSlotId);
                if (slotIndex !== -1) {
                    updatedSlots[slotIndex] = {
                        ...updatedSlots[slotIndex],
                        isOccupied: true,
                        status: "occupied",
                    };
                } else {
                    // If latest booked slot isn't in parking slots, add it
                    updatedSlots.push({
                        id: latestBookedSlotId,
                        isOccupied: true,
                        status: "occupied",
                    });
                }
            }

            // Set the updated slots in state
            setParkingSlots(updatedSlots);
            return updatedSlots; // Return the slots for use in other functions
        } catch (error) {
            console.error("Error fetching data:", error);
            setError(error.message);
            return []; // Return empty array as fallback
        } finally {
            setLoading(false);
        }
    };

    // Fetch all data on mount & refresh every 3 minutes
    useEffect(() => {
        fetchAllData();
        const interval = setInterval(fetchAllData, 180000);
        return () => clearInterval(interval);
    }, []);

    // Updated handlePayment to include date
    const handlePayment = async (slotId) => {
        // Always use current date if no date is selected
        const finalSelectedDate = selectedDate || new Date();

        if (!selectedTimeSlot) {
            alert("Please select a time slot before proceeding with payment.");
            return;
        }

        const options = {
            key: "rzp_test_8aHz8s9WnoELui",
            amount: 500 * 100, 
            currency: "INR",
            name: "ParkEz",
            description: `Parking Slot Payment: ${finalSelectedDate.toLocaleDateString()} ${selectedTimeSlot}`,
            handler: async function (response) {
                try {
                    console.log("Payment Successful:", response);

                    // Send booking details to the backend
                    const slotRes = await fetch(`http://localhost:5001/api/reserved/${slotId}`, {
                        method: "PATCH",
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${localStorage.getItem('token')}`
                        },
                        body: JSON.stringify({
                            isOccupied: true,
                            paymentId: response.razorpay_payment_id,
                            timeSlot: selectedTimeSlot,
                            bookingDate: finalSelectedDate.toISOString().split('T')[0] // Send date in YYYY-MM-DD format
                        }),
                    });

                    if (!slotRes.ok) {
                        throw new Error('Failed to update slot status');
                    }

                    await fetchAllData();
                    alert(`Payment successful! Your slot has been reserved for ${finalSelectedDate.toLocaleDateString()} ${selectedTimeSlot}`);
                } catch (error) {
                    console.error("Payment processing error:", error);
                    alert("Payment succeeded but error updating reservation. Contact support.");
                }
            },
            prefill: {
                name: "Aayan Mulla",
                email: "aayanmulla10@gmail.com",
                contact: "7083076077"
            },
            theme: {
                color: "#3399cc"
            }
        };

        try {
            const rzp1 = new window.Razorpay(options);
            rzp1.open();
        } catch (error) {
            console.error("Error opening payment dialog:", error);
            alert("Failed to initialize payment. Please try again.");
        }
    };
    // Handle date change
    const handleDateChange = (date) => {
        setSelectedDate(date);
        // Reset selected slot and time slot when date changes
        setSelectedSlotId(null);
        setSelectedTimeSlot("");
    };

    return (
        <div className="bookings-container">
            <Navbar />
            <div className="bookings-content">
                <h1 className="bookings-heading">Ajeenkya DY Patil Parking Zone</h1>

                {loading ? (
                    <p>Loading parking slots...</p>
                ) : error ? (
                    <p className="error-message">{error}</p>
                ) : (
                    <div>
                        {/* Date Picker */}
                        <div className="date-picker-container">
                            <label>Select Booking Date:</label>
                            <DatePicker
                                selected={selectedDate}
                                onChange={handleDateChange}
                                minDate={new Date()} // Prevent selecting past dates
                                placeholderText="Choose a date"
                                className="form-control" // Optional: for bootstrap-like styling
                            />
                        </div>

                        <div className="bookings-grid">
                            {parkingSlots.map((slot) => {
                                const isOccupied = slot.isOccupied || bookedSlotId === slot.id;

                                return (
                                    <div
                                        key={slot.id}
                                        className={`parking-slot ${
                                            slot.isOccupied === null
                                                ? "under-construction"
                                                : isOccupied
                                                ? "occupied"
                                                : "available"
                                        }`}
                                        onClick={() => {
                                            if (!slot.isOccupied && slot.isOccupied !== null) {
                                                setSelectedSlotId(slot.id);
                                            }
                                        }}
                                    >
                                        <span>Slot {slot.id}</span>
                                        <span>
                                            {slot.isOccupied === null
                                                ? "Under Construction"
                                                : isOccupied
                                                ? "Occupied"
                                                : "Available"}
                                        </span>

                                        {selectedSlotId === slot.id && !isOccupied && selectedDate && (
                                            <div className="time-slot-selection">
                                                <label>Select Time Slot:</label>
                                                <select
                                                    value={selectedTimeSlot}
                                                    onChange={(e) => setSelectedTimeSlot(e.target.value)}
                                                >
                                                    <option value="">-- Select --</option>
                                                    <option value="9 AM - 12 PM">9 AM - 12 PM</option>
                                                    <option value="12 PM - 3 PM">12 PM - 3 PM</option>
                                                    <option value="3 PM - 6 PM">3 PM - 6 PM</option>
                                                    <option value="6 PM - 9 PM">6 PM - 9 PM</option>
                                                </select>
                                                <button onClick={() => handlePayment(slot.id)}>Book Now</button>
                                            </div>
                                        )}
                                    </div>
                                );
                            })}
                        </div>
                    </div>
                )}
            </div>
            <Reservation />
            <Footer />
        </div>
    );
};

export default Bookings;
