import React, { useEffect, useState, useRef } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Bookings.css";
import Navbar from "../NavBar/NavBar";
import Reservation from "../Reservations/Reservation";
import Footer from "../Footer/Footer";
import { FaCalendarAlt } from "react-icons/fa";

const Bookings = () => {
    const [parkingSlots, setParkingSlots] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [bookedSlotId] = useState(null);
    const [selectedSlotId, setSelectedSlotId] = useState(null);
    const [selectedTimeSlot, setSelectedTimeSlot] = useState(""); 
    const [selectedDate, setSelectedDate] = useState(null);
    const [showDatePicker, setShowDatePicker] = useState(false);
    const datePickerRef = useRef(null);

    // Close date picker when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (datePickerRef.current && !datePickerRef.current.contains(event.target)) {
                setShowDatePicker(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    const toggleDatePicker = () => {
        setShowDatePicker(!showDatePicker);
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
        setShowDatePicker(false);
        setSelectedSlotId(null);
        setSelectedTimeSlot("");
    };

    // Rest of your existing code (fetchAllData, handlePayment, etc.) remains the same
    // ...

    return (
        <div className="bookings-container">
            <Navbar />
            <div className="bookings-content">
                <h1 className="bookings-heading">Ajeenkya DY Patil Parking Zone</h1>

                {loading ? (
                    <p>Loading parking slots...</p>
                ) : error ? (
                    <p className="error-message">{error}</p>
                ) : (
                    <div>
                        {/* Custom Date Picker */}
                        <div className="custom-date-picker-container" ref={datePickerRef}>
                            <div className="calendar-icon-container" onClick={toggleDatePicker}>
                                <FaCalendarAlt className="calendar-icon" />
                                {selectedDate && (
                                    <span className="selected-date">
                                        {selectedDate.toLocaleDateString()}
                                    </span>
                                )}
                            </div>

                            {showDatePicker && (
                                <div className="date-picker-dropdown">
                                    <DatePicker
                                        selected={selectedDate}
                                        onChange={handleDateChange}
                                        minDate={new Date()}
                                        inline
                                        calendarClassName="dropdown-calendar"
                                    />
                                </div>
                            )}
                        </div>

                        {/* Rest of your existing JSX remains the same */}
                        <div className="bookings-grid">
                            {/* ... */}
                        </div>
                    </div>
                )}
            </div>
            <Reservation />
            <Footer />
        </div>
    );
};

export default Bookings;
